mapper.select:
SELECT

FROM device_log

    is_del = '0'

        dict_dev_log_type=#{devLogType}



        AND (
        fk_dev_serial_num LIKE #{pattern}
        OR content LIKE #{pattern}
        )

mapper.update:
update device_log
set is_del=1
WHERE id = #{id}

update device_log
set fk_dev_serial_num = #{devSerialNum},
    dict_dev_log_type = #{devLogType},
    content           = #{content}
WHERE id = #{id}
